import React from "react";
import "./WalletPicker.css";
import { useState} from 'react';

function WalletPicker({ setOpenModal, operation }) {

let Wallets = []
    for (const [key, value] of Object.entries(window.cardano)) {
        if (value.icon && key !== "ccvault" && key !== "typhoncip30" ){
            console.log(key)
            Wallets.push(key)
        }
      }
      
  let wal =  Wallets.map( (item, index) => 
  <div className="walletOption" key={index}>
    <div className="walletOptionBtn" onClick={ () => operation(item)} >
        {item}
        { (item==="nami") ? <img className="walletOptionImg" src="data:image/svg+xml;base64," /> 
        : <img className="walletOptionImg" src={window.cardano[item].icon.replace(/\s/g, ';')} />}    
    </div>
    </div>)

  return (
    <div className="modalBackground" onClick={() => { setOpenModal(false); }}>
      <div className="modalContainer"  >
        <div className="titleCloseBtn">
          <button
            onClick={() => {
              setOpenModal(false);
            }}
          >
            X
          </button>
        </div>
  
        <div className="title">
          <h1>Select Wallet</h1>
        </div>
        <div className="body">
        {wal}
            
        </div>
        <div className="footer">
          <button
            onClick={() => {
              setOpenModal(false);
            }}
            id="cancelBtn">
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
}

export default WalletPicker;